import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
    {name: 'Max', age: 28},
    {name: 'Manu', age: 29},
    {name: 'Stephanie', age: 26}
  ]
}

switchNameHandler = () => {
  //console.log('Was clicked!');
  this.state.persons[0].name = 'Maximillian';
}

render() {
  return (  //uses jsx to convert html to javascript
    <div className="App">
   <h1> Hi! This is a react app! </h1>
   <p> This is really working</p>
   <button onClick={this.switchNameHandler}> Switch Name </button>
    <Person name= {this.state.persons[0].name} age={this.state.persons[0].age}/>   
    <Person name= {this.state.persons[1].name} age={this.state.persons[1].age}>My hobbies: racing</Person>  
    <Person name = {this.state.persons[2].name} age = {this.state.persons[2].age}/>  
    </div>
    //jsx element must have one root element (can't return more than one)
  //React.createElement('div', null, React.createElement('h1', 'Does this work?'))
  );
}
}

export default App;
