import React from 'react';
import './Person.css';
//import Radium from 'radium';

const person = (props) => {
    const style = {
        '@media (min-width: 500px)': {
            width: '450px'
        }
    };
    return (
        <div className = 'Person' style = {style}>
            <p onClick= {props.click}> I'm {props.name}, and I am {props.age} years old</p>
            <p> {props.children}</p>
            <input type = "text" onChange= {props.changed} value={props.name}/>
    </div>
    )
};

//if you have dynamic content, wrap in single curly braces
// use for dynamic number 1-30 {Math.floor(Math.random()*30)}

export default (person);
//while sending the export through Radium is enough for sudo selectors
//for media queries or animations w/ key frames, must wrap the whole app 
//cant just send it through Radium, must wrap
