import React, {Component} from 'react';
import './App.css';
import Person from './Person/Person';

class App extends Component {
  state = {
    persons: [
    {name: 'Max', age: 28},
    {name: 'Manu', age: 29},
    {name: 'Stephanie', age: 26}
  ],
  otherState: 'some other value'
}

switchNameHandler = (newName) => {
  //console.log('Was clicked!');
  //this.state.persons[0].name = 'Maximillian'; don't do this
  this.setState( {
    persons: [
      {name: newName, age: 28},
      {name: 'Manu', age: 29},
      {name: 'Stephanie', age: 27}
    ]
   })
}

nameChangedHandler = (event) => {
  this.setState( {
  persons: [
    {name: 'Max', age: 28},
    {name: event.target.value, age: 29},
    {name: 'Stephanie', age: 26}
  ]
} )
}

render() {
  const style = {
    backgroundColor: 'white',
    font: 'inherit',
    border: '1px solid blue',
    padding: '8px'
  };
  return (  //uses jsx to convert html to javascript
    <div className="App">
   <h1> Hi! This is a react app! </h1>
   <p> This is really working</p>
   <button 
   style = {style}
   onClick={ ()=> this.switchNameHandler('Maximillian!')}> Switch Name </button>
    <Person
     name= {this.state.persons[0].name} 
     age={this.state.persons[0].age}/>   
    <Person 
    name= {this.state.persons[1].name} 
    age={this.state.persons[1].age}
    click = {this.switchNameHandler.bind(this, 'Max!')}
    changed = {this.nameChangedHandler}> 
    My hobbies: racing 
    </Person>  
    <Person 
    name = {this.state.persons[2].name} 
    age = {this.state.persons[2].age}/>  
    </div>
    //jsx element must have one root element (can't return more than one)
  //React.createElement('div', null, React.createElement('h1', 'Does this work?'))
  );
}
}

export default App;
