import React, {useEffect, useRef} from 'react';

import './Cockpit.css';

const cockpit = (props) => {
/*
    const toggleBtnRef = useRef(null);
    toggleBtnRef.current.click(); */
/*
    // if you want to base it on props then useState()
 useEffect(() => { 
    //pass a function to useEffect and it will render
    //every time cockpit runs
    //rerenders for every update
    //can send http request in here
     console.log('[Cockpit.js] useEffect');
    setTimeout(() => {
        alert('Saved data to cloud!');
    }, 1000); 
}, [props.persons]); //can useEffect as many times as u want
//send an empty array if you only want to see the first change
*/

let btnClass = 'button';
if (props.showPersons)
{
    btnClass = 'buttonRed';
}
    const assignedClasses = [];
 if (props.personsLength <= 2) {
  assignedClasses.push('red'); 
 }
 if( props.personsLength <= 1) {
  assignedClasses.push('bold'); //classes will be red and bold
 }

    return (
        <div>
            <h1> {props.title} </h1>
            <p className = {assignedClasses.join(' ')}> This is really working</p>
            <button className = {btnClass}
            onClick={props.clicked}> 
            Toggle Persons 
            </button>
        </div>
        )

};
    // ref = {toggleBtnRef} 
export default React.memo(cockpit); //optimises functional components that 
//do not update with every change to the parent component
