import React from 'react';
import  './Question.css';

const question = ( props ) => {


    //const rnd = Math.random();

    //if ( rnd > 0.7 ) {
    //    throw new Error( 'Something went wrong' );
    //}
    //onClick={props.click}
    //<p>{props.children}</p>
    var questionFormat = null;
    if (props.qType === "ni"){
        questionFormat = 
            (<div className={'question'}>
            <p >{props.question} </p>
            <input type="text" onChange={props.changed} value = '' />
            </div>);
    }
    else if (props.qType === "dd") {
        questionFormat = 
        (
        <div className={'question'}>
                <p >{props.question} </p>
                <div className="dropdown">
                    <button className="dropbtn">Dropdown</button>
                    <div className="dropdown-content">
                        {props.incorrect.map((answer, index) => {
                            return <p>{answer}</p>
                        })}
                    </div>
                </div>
        </div>);
    }
    else if(props.qType === "mc") {
        questionFormat = 
            (<div className={'question'}>
            <p >{props.question} </p>
            <p>{props.incorrect}</p>
            </div>);
    }
    else {
        questionFormat = 
            (<div className={'question'}>
            <p >{props.question} </p>
            <p>{props.incorrect}</p>
            </div>);
    }

    return (
        <div>

            <div>
                    {questionFormat}
            </div>
        </div>
    )
};

export default question;
