import React, {Component} from 'react';
//import styled from 'styled-components';
import classes from './App.css';
import Person from './Person/Person';
import ErrorBoundary from './ErrorBoundary/ErrorBoundary';
//import Radium, {StyleRoot} from 'radium';

class App extends Component {
  state = {
    persons: [
    {id: 'aoieahi', name: 'Max', age: 28},
    {id: 'nfaioen', name: 'Manu', age: 29},
    {id: 'nfaogne', name: 'Stephanie', age: 26}
  ],
  otherState: 'some other value',
  showPersons: false
}

nameChangedHandler = ( event, id ) => {
  const personIndex = this.state.persons.findIndex(p => {
    return p.id === id;
  });

const person ={
   ...this.state.persons[personIndex]
};

//you can also use Object.assign({}, object)

person.name = event.target.value;

const persons = [...this.state.persons];
persons[personIndex]= person;

this.setState( {persons: persons});
}

deletePersonHandler = (personIndex) => {
  //const persons = this.state.persons.slice();
  const persons = [...this.state.persons];
  persons.splice(personIndex,1);
  this.setState({persons: persons})
}

//ensures the this keyword refers to this class
togglePersonsHandler = () => {
  const doesShow = this.state.showPersons;
  this.setState({showPersons: !doesShow});
}
 render() {
  const style = {
    backgroundColor: 'green',
    color: 'white',
    font: 'inherit',
    border: '1px solid blue',
    padding: '8px',
    cursor: 'pointer',
    ':hover': {
      backgroundColor: 'lightgreen',
      color: 'black'
    } //sudo selectors are supported by radium
  }; 

  let persons = null;
  let buttonStatus = 'button';

  if (this.state.showPersons) {
    persons = (
      <div >
        {this.state.persons.map((person, index) => {
          return <ErrorBoundary key = {person.id}>
            <Person 
              click = {() => this.deletePersonHandler(index)}
              name = {person.name} 
              age = {person.age}
              changed = {(event) => this.nameChangedHandler(event, person.id)}/> 
           </ErrorBoundary>
        })} 
    </div> 
    )
    style.backgroundColor = 'red';
    style[':hover'] = {
      backgroundColor: 'salmon',
      color: 'black'
    };

    buttonStatus = 'buttonRed';
  }

 const assignedClasses = [];
 if (this.state.persons.length <= 2) {
  assignedClasses.push(classes.red); 
 }
 if( this.state.persons.length <= 1) {
  assignedClasses.push(classes.bold); //classes will be red and bold
 }

  return (  //uses jsx to convert html to javascript
  //<StyleRoot>
    <div className= 'App' >
      <h1> Hi! This is a react app! </h1>
      <p className = {assignedClasses.join(' ')}> This is really working</p>
      <button className = {buttonStatus}
      onClick={ ()=> this.togglePersonsHandler('Maximillian!')}> 
      Toggle Persons </button>
      {persons}
     </div>
  //</StyleRoot>
    //jsx element must have one root element (can't return more than one)
  //React.createElement('div', null, React.createElement('h1', 'Does this work?'))
  );
}
}

export default (App); //higher order component
