import React, { Component } from 'react';
import QuestionData from './Question/questions.json';
import './App.css';
import Question from './Question/Question';


class App extends Component {
  state = {
    questions: [

    ],
    showInstructions: false
  }
  createQuestionsArray = () => {
    let questionsList = [];
      QuestionData.map((question, index)=>{
        return questionsList.push( question);

      }) 
        var randomNums = [];
        var questions5 = [];
    while(randomNums.length < 5){
      var r = Math.floor(Math.random() * 50);
      if(randomNums.indexOf(r) === -1){
        randomNums.push(r);
        questions5.push(questionsList[r]);
      }
      //sreturn this.state.questions;
      ;}
      this.setState( {questions: questions5 } );  

  };



 inputChangedHandler = ( event, id ) => {
    const questionIndex = this.state.questions.findIndex( q => {
      return q.id === id;
    } );

    const question = {
      ...this.state.questions[questionIndex]
    };

    question.incorrect = event.target.value;

    const questions = [...this.state.questions];
    questions[questionIndex] = question;

    this.setState( { questions: questions } );
  }

  toggleInstructionsHandler = () => {
    const doesShow = this.state.showInstructions;
    this.setState( { showInstructions: !doesShow } );
  }

  render () {
    let instructions = null;

    if ( this.state.showInstructions ) {
      instructions = (<div>
        <br></br>
        <br></br>
        Welcome to the FBLA 5 Question Quiz! <br></br>
        Please fill out the following 5 questions <br></br>
        and click submit to receive your answer. <br></br>
        Refresh to receive a new set of questions. <br></br>
        <br></br>
        <br></br>
      </div>);

    }

    let questions = (
      <div>
        {this.state.questions.map( ( question, index ) => {

         return <Question
          id = {question.id}
          question={question.question}
          answer={question.answer}
          incorrect = {question.incorrect}
          qType = {question.qType}
          changed={( event ) => this.inputChangedHandler( event, question.id )} 
          /> 
        } )}

      </div>
    )

    let submit = (   
        <button>
        Submit
      </button>)

    return (
      <div className={'App'}>
        <h1>FBLA 5 Question Quiz</h1>
        <h3>Test Your Knowledge of FBLA and its History</h3>

        <button
          className={'btnClass'}
          onClick={this.toggleInstructionsHandler}>Click To Open/Close Instructions</button>
        {instructions}
        <button
          onClick = {this.createQuestionsArray}> Click to Generate Questions
        </button>
        {questions}
        {submit}
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}

export default App;
