import React from 'react';
import  './Question.css';

const question = ( props ) => {


    //const rnd = Math.random();

    //if ( rnd > 0.7 ) {
    //    throw new Error( 'Something went wrong' );
    //}
    //onClick={props.click}
    //<p>{props.children}</p>
    var questionFormat = null;
    if (props.qType === "ni"){
        questionFormat = 
            (<div className={'question'} >
            <p >{props.question} </p>
            <input type="text" onChange={props.changed} value = {props.incorrect} />
            </div>);
    }
    else if (props.qType === "dd") {
        questionFormat = 
        (
        <div className={'question'}>
                <p >{props.question} </p>
                <div className="dropdown">
                    <button className="dropbtn">Dropdown</button>
                    <div className="dropdown-content">
                        {props.incorrect.map((option, index) => {
                            return <div >
                                <input type= "radio" name = {props.id} />
                                <label>{option}</label>
                                </div>
                        })}
                    </div>
                </div>
        </div>);
    }
    else  {
        questionFormat = 
            (<div className={'question'}>
            <p >{props.question} </p>
                {props.incorrect.map((option, index) => {
                    return <div  align = 'left'>
                        <input type="radio"  name = {props.id} />
                        <label>{option}</label>

                    </div>
            })}

            </div>);
    }


    return (
        <div>

            <div>
                {questionFormat}
            </div>
        </div>
    )
};

export default question;
