import React, {useEffect} from 'react';

import './Cockpit.css';

const cockpit = (props) => {
// if you want to base it on props then useState()
 useEffect(() => { 
    //pass a function to useEffect and it will render
    //every time cockpit runs
    //rerenders for every update
    //can send http request in here
     console.log('[Cockpit.js] useEffect');

 });

let btnClass = 'button';
if (props.showPersons)
{
    btnClass = 'buttonRed';
}
    const assignedClasses = [];
 if (props.persons.length <= 2) {
  assignedClasses.push('red'); 
 }
 if( props.persons.length <= 1) {
  assignedClasses.push('bold'); //classes will be red and bold
 }

    return (
        <div>
            <h1> {props.title} </h1>
            <p className = {assignedClasses.join(' ')}> This is really working</p>
            <button className = {btnClass}
            onClick={props.clicked}> 
            Toggle Persons 
            </button>
        </div>
        )

};

export default cockpit;
