    persons: [
    {id: 'aoieahi', name: 'Max', age: 28},
    {id: 'nfaioen', name: 'Manu', age: 29},
    {id: 'nfaogne', name: 'Stephanie', age: 26}
  ],
  otherState: 'some other value',
  showPersons: false
}

nameChangedHandler = (event) => {
  this.setState( {
  persons: [
    {name: 'Max', age: 28},
    {name: event.target.value, age: 29},
    {name: 'Stephanie', age: 26}
  ]
} )
}
