import React, { useState } from 'react';
import './App.css';
import Person from './Person/Person';


const app = props => {
  const [personsState, setPersonsState] = useState( {
    persons: [
      {name: 'Max', age: 28},
      {name: 'Manu', age: 29},
      {name: 'Stephanie', age: 26}
    ],
    otherState: 'some other value' 
  } ) ;

  const switchNameHandler = () => {
    //console.log('Was clicked!');
    //this.state.persons[0].name = 'Maximillian'; don't do this
    setPersonsState( {
      persons: [
        {name: 'Maximillian', age: 28},
        {name: 'Manu', age: 29},
        {name: 'Stephanie', age: 27}
      ]
     } )
  }

  return (  
    //uses jsx to convert html to javascript
    <div className="App">
    <h1> Hi! This is a react app! </h1>
    <p> This is really working</p>
    <button onClick={switchNameHandler}> Switch Name </button>
    <Person 
    name= {personsState.persons[0].name} 
    age={personsState.persons[0].age}
    />   
    <Person 
    name= {personsState.persons[1].name} 
    age={personsState.persons[1].age}>
    My hobbies: racing
    </Person>  
    <Person 
    name = {personsState.persons[2].name} 
    age = {personsState.persons[2].age}
    />  
    </div>
    //jsx element must have one root element (can't return more than one)
  //React.createElement('div', null, React.createElement('h1', 'Does this work?'))
  );
}


export default app;
