/*
import React from 'react';
import './Person.css';
//import Radium from 'radium';
import styled from 'styled-components';

const StyledDiv = styled.div`
width: 60%;
margin: 16px;
border: 1px solid #eee;
box-shadow: 0 2px 3px #ccc;
padding: 16px;
text-align: center;

@media (min-width: 500px) {
    width: 450px;
}
`;

/*
const person = (props) => {
    const style = {
        '@media (min-width: 500px)': {
            width: '450px'
        }
    };
    return (
       // <div className = 'Person' style = {style}>
        <StyledDiv>
            <p onClick= {props.click}> I'm {props.name}, and I am {props.age} years old</p>
            <p> {props.children}</p>
            <input type = "text" onChange= {props.changed} value={props.name}/>
        </StyledDiv>
    )
};
*/

//if you have dynamic content, wrap in single curly braces
// use for dynamic number 1-30 {Math.floor(Math.random()*30)}

//export default (person);

//while sending the export through Radium is enough for sudo selectors
//for media queries or animations w/ key frames, must wrap the whole app 
//cant just send it through Radium, must wrap 

import React, {Component} from 'react';
import Aux from '../../../hoc/Auxiliary';
import classes from './Person.css';
import PropTypes from 'prop-types';
import AuthContext from '../../../context/auth-context';
const person = props => {

  console.log('[Person.js] rendering ...');
  const func = () => {};
/*    const rnd = Math.random();

    if ( rnd > 0.7 ) {
        throw new Error('Something went wrong!');
    }
*/ //can use React.fragment instead or <fragment> after importing
//the fragment
//aux is a higher order component (hoc)
  return (
    <Aux> 
      <div align = 'center'>
      <div className={'person'} >
        <AuthContext.Consumer>
          {func = (context) => {context.authenticated ? 
          <p> Authenticated </p> 
          : <p> Please log in</p>}}
        </AuthContext.Consumer>
         <p onClick={props.click}>
         I'm {props.name} and I am {props.age} years old!
         </p>
          <p>{props.children}</p>
         <input type="text" onChange={props.changed} value={props.name} />
     </div>
     </div>
    </Aux> //aux component is an empty wrapper
  );
};
//checks that the proper props are passed through
person.propTypes = {
  click: PropTypes.func,
  name: PropTypes.string,
  age: PropTypes.number,
  changed: PropTypes.func
}
export default person;
