import React, { Component } from 'react';
import classes from './App.css';
import Question from './Question/Question';
import ErrorBoundary from './ErrorBoundary/ErrorBoundary';

class App extends Component {
  state = {
    questions: [
      { id: 'asfa1', name: 'Max', age: 28 },
      { id: 'vasdf1', name: 'Manu', age: 29 },
      { id: 'asdf11', name: 'Stephanie', age: 26 }
    ],
    otherState: 'some other value',
    showInstructions: false
  }

  nameChangedHandler = ( event, id ) => {
    const questionIndex = this.state.questions.findIndex( q => {
      return q.id === id;
    } );

    const question = {
      ...this.state.questions[questionIndex]
    };

    question.name = event.target.value;

    const questions = [...this.state.questions];
    questions[questionIndex] = question;

    this.setState( { questions: questions } );
  }

  toggleInstructionsHandler = () => {
    const doesShow = this.state.showInstructions;
    this.setState( { showInstructions: !doesShow } );
  }

  render () {
    let questions = null;
    let btnClass = '';

    if ( this.state.showInstructions ) {
      questions = (
        <div>
          {this.state.questions.map( ( question, index ) => {
            return <ErrorBoundary key={question.id}>
              <Question
                name={question.name}
                age={question.age}
                changed={( event ) => this.nameChangedHandler( event, question.id )} />
            </ ErrorBoundary>
          } )}
        </div>
      );

      btnClass = classes.Red;
    }
    questions = (
      <div>
        {this.state.questions.map( ( question, index ) => {
          return <ErrorBoundary key={question.id}>
            <Question
              name={question.name}
              age={question.age}
              changed={( event ) => this.nameChangedHandler( event, question.id )} />
          </ ErrorBoundary>
        } )}
      </div>
    )

    return (
      <div className={'App'}>
        <h1>FBLA 5 Question Quiz</h1>
        <h3>Test Your Knowledge of FBLA and its History</h3>
        <button
          className={btnClass}
          onClick={this.toggleInstructionsHandler}>Click To Open/Close Instructions</button>
        {questions}
      </div>
    );
    // return React.createElement('div', {className: 'App'}, React.createElement('h1', null, 'Does this work now?'));
  }
}

export default App;
